import React from 'react';
import 'dragula/dist/dragula.css';
import Swimlane from './Swimlane';
import './Board.css';
import dragula from 'dragula';

export default class Board extends React.Component {
  constructor(props) {
    super(props);
    this.swimlanes = {
      backlog: React.createRef(),
      inProgress: React.createRef(),
      complete: React.createRef(),
    };
  }

  componentDidMount() {
    this.drake = dragula([
      this.swimlanes.backlog.current,
      this.swimlanes.inProgress.current,
      this.swimlanes.complete.current,
    ]);
    this.drake.on('drop', (el, target, source, sibling) =>
      this.updateClient(el, target, source, sibling)
    );
  }

  componentWillUnmount() {
    this.drake.remove();
  }

  updateClient(el, target, _, sibling) {
    this.drake.cancel(true);

    let targetSwimlane = 'backlog';
    if (target === this.swimlanes.inProgress.current) {
      targetSwimlane = 'in-progress';
    } else if (target === this.swimlanes.complete.current) {
      targetSwimlane = 'complete';
    }

    const clientsList = [
      ...this.props.clients.backlog,
      ...this.props.clients.inProgress,
      ...this.props.clients.complete,
    ];

    const clientThatMoved = clientsList.find(client => client.id === el.dataset.id);
    const clientThatMovedClone = { ...clientThatMoved, status: targetSwimlane };

    const updatedClients = clientsList.filter(client => client.id !== clientThatMoved.id);

    const index = updatedClients.findIndex(client => sibling && client.id === sibling.dataset.id);
    updatedClients.splice(index === -1 ? updatedClients.length : index, 0, clientThatMovedClone);

    this.props.updateClients({
      backlog: updatedClients.filter(c => c.status === 'backlog'),
      inProgress: updatedClients.filter(c => c.status === 'in-progress'),
      complete: updatedClients.filter(c => c.status === 'complete'),
    });
  }

  getCardStyle(status) {
    switch (status) {
      case 'backlog':
        return { backgroundColor: 'lightgray', border: '1px solid #ccc', padding: '10px', margin: '5px' };
      case 'in-progress':
        return { backgroundColor: 'lightblue', border: '1px solid #ccc', padding: '10px', margin: '5px' };
      case 'complete':
        return { backgroundColor: 'lightgreen', border: '1px solid #ccc', padding: '10px', margin: '5px' };
      default:
        return {};
    }
  }

  renderSwimlane(name, clients, ref) {
    return (
      <Swimlane
        name={name}
        clients={clients.map(client => ({
          ...client,
          style: this.getCardStyle(client.status),
        }))}
        dragulaRef={ref}
      />
    );
  }

  render() {
    const { backlog, inProgress, complete } = this.props.clients;

    return (
      <div className="Board">
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-4">
              {this.renderSwimlane('Backlog', backlog, this.swimlanes.backlog)}
            </div>
            <div className="col-md-4">
              {this.renderSwimlane('In Progress', inProgress, this.swimlanes.inProgress)}
            </div>
            <div className="col-md-4">
              {this.renderSwimlane('Complete', complete, this.swimlanes.complete)}
            </div>
          </div>
        </div>
      </div>
    );
  }
}

