import React from 'react';
import './Card.css';

export default class Card extends React.Component {
  render() {
    let className = ['Card'];

    // Apply different styles based on the card's status
    if (this.props.status === 'backlog') {
      className.push('Card-grey');
    } else if (this.props.status === 'in-progress') {
      className.push('Card-blue');
    } else if (this.props.status === 'complete') {
      className.push('Card-green');
    }

    return (
      <div
        className={className.join(' ')}
        data-id={this.props.id}
        data-status={this.props.status}
        data-swimlane={this.props.status}  // Added data-swimlane to use in drag-and-drop
      >
        <div className="Card-title">{this.props.name}</div>
        <div>{this.props.description}</div>
      </div>
    );
  }
}
