import React from 'react';
import Dragula from 'dragula';
import 'dragula/dist/dragula.css';
import Swimlane from './Swimlane';
import './Board.css';

export default class Board extends React.Component {
  constructor(props) {
    super(props);
    const clients = this.getClients();
    this.state = {
      clients: {
        backlog: clients.filter(client => client.status === 'backlog'),
        inProgress: clients.filter(client => client.status === 'in-progress'),
        complete: clients.filter(client => client.status === 'complete'),
      }
    };

    this.swimlanes = {
      backlog: React.createRef(),
      inProgress: React.createRef(),
      complete: React.createRef(),
    };
  }

  // Function to get initial list of clients
  getClients() {
    return [
      // Your initial client data here
    ].map(companyDetails => ({
      id: companyDetails[0],
      name: companyDetails[1],
      description: companyDetails[2],
      status: companyDetails[3],
    }));
  }

  // Handle card status update when dropped
  updateCardStatus(cardId, newStatus) {
    const updatedClients = { ...this.state.clients };

    Object.keys(updatedClients).forEach(swimlane => {
      const clientIndex = updatedClients[swimlane].findIndex(client => client.id === cardId);
      if (clientIndex !== -1) {
        updatedClients[swimlane][clientIndex].status = newStatus;
      }
    });

    this.setState({ clients: updatedClients });
  }

  // Initialize Dragula for drag-and-drop functionality
  initializeDragula() {
    Object.keys(this.swimlanes).forEach(swimlane => {
      const container = this.swimlanes[swimlane].current;

      if (container) {
        const drake = Dragula([container]);

        // On drop, update the status based on the new swimlane
        drake.on('drop', (el, target) => {
          const cardId = el.dataset.id;
          const newSwimlane = target.dataset.swimlane;
          this.updateCardStatus(cardId, newSwimlane);
        });
      }
    });
  }

  componentDidMount() {
    this.initializeDragula();
  }

  // Render swimlanes with their respective clients
  renderSwimlane(name, clients, ref) {
    return (
      <Swimlane
        name={name}
        clients={clients}
        dragulaRef={ref}
        key={name}
      />
    );
  }

  render() {
    return (
      <div className="Board">
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-4">
              {this.renderSwimlane('Backlog', this.state.clients.backlog, this.swimlanes.backlog)}
            </div>
            <div className="col-md-4">
              {this.renderSwimlane('In Progress', this.state.clients.inProgress, this.swimlanes.inProgress)}
            </div>
            <div className="col-md-4">
              {this.renderSwimlane('Complete', this.state.clients.complete, this.swimlanes.complete)}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
