import React from 'react';
import { Droppable } from 'react-beautiful-dnd';
import Card from './Card';
import './Swimlane.css';

export default class Swimlane extends React.Component {
  render() {
    const { name, status, clients, getCardStyle } = this.props;

    return (
      <div className="Swimlane-column">
        <div className="Swimlane-title">{name}</div>
        <Droppable droppableId={status} direction="vertical">
          {(provided) => (
            <div
              className="Swimlane-dragColumn"
              ref={provided.innerRef}
              {...provided.droppableProps}
            >
              {clients.map((client, index) => (
                <Card
                  key={client.id}
                  id={client.id}
                  name={client.name}
                  status={client.status}
                  style={getCardStyle(client.status)} // Get dynamic background color from parent
                  index={index} // Pass index for drag-and-drop sorting
                />
              ))}
              {provided.placeholder} {/* Reacts to the drag-and-drop space */}
            </div>
          )}
        </Droppable>
      </div>
    );
  }
}


