import React from 'react';
import Card from './Card';
import './Swimlane.css';
import Dragula from 'dragula';

export default class Swimlane extends React.Component {
  componentDidMount() {
    const { dragulaRef, onDragEnd } = this.props;
    const drake = Dragula([dragulaRef.current]);

    drake.on('drop', (el, target) => {
      const newStatus = target.getAttribute('data-status');
      onDragEnd({ target: el }, newStatus); // Trigger status update on drop
    });
  }

  render() {
    const cards = this.props.clients.map(client => (
      <Card
        key={client.id}
        id={client.id}
        name={client.name}
        description={client.description}
        status={client.status}
      />
    ));

    return (
      <div
        className="Swimlane-column"
        data-status={this.props.name.toLowerCase()}
        ref={this.props.dragulaRef}
      >
        <div className="Swimlane-title">{this.props.name}</div>
        <div className="Swimlane-dragColumn">
          {cards}
        </div>
      </div>
    );
  }
}
