// HomeTab.js
import React, { useState } from 'react';

export default function HomeTab({ onUnlock }) {
  const [userId, setUserId] = useState('');
  const [pin, setPin] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handleUserIdChange = (e) => setUserId(e.target.value);
  const handlePinChange = (e) => setPin(e.target.value);

  const handleUnlockClick = () => {
    if (!userId || isNaN(userId)) {
      setErrorMessage('Please enter a valid User ID.');
      return;
    }

    if (pin.length !== 4 || isNaN(pin)) {
      setErrorMessage('Please enter a valid 4-digit PIN.');
      return;
    }

    setErrorMessage('');
    onUnlock(pin, Number(userId));
  };

  return (
    <header className="App-header">
      <div className="App-title">
        <h1>SHIPTIVITAS</h1>
        <div className="App-icon">
          <div className="Circle"></div>
        </div>
      </div>

      <div className="pin-input-container" style={{ marginTop: '20px' }}>
        <h3>Enter your User ID and 4-digit PIN to unlock Shipping Requests</h3>
        <input
          type="text"
          value={userId}
          onChange={handleUserIdChange}
          placeholder="User ID"
          style={{
            padding: '8px',
            fontSize: '16px',
            width: '100px',
            marginRight: '10px',
            textAlign: 'center',
          }}
        />
        <input
          type="text"
          value={pin}
          onChange={handlePinChange}
          maxLength="4"
          placeholder="PIN"
          style={{
            padding: '8px',
            fontSize: '16px',
            width: '100px',
            textAlign: 'center',
          }}
        />
        <button
          onClick={handleUnlockClick}
          style={{ marginLeft: '10px', padding: '8px 16px', fontSize: '16px' }}
        >
          Unlock
        </button>
        {errorMessage && (
          <div style={{ color: 'red', marginTop: '10px' }}>
            {errorMessage}
          </div>
        )}
      </div>
    </header>
  );
}


