import React, { useState } from 'react';

export default function PinVerification({ onUnlock }) {
  const [pin, setPin] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const handlePinChange = (e) => {
    setPin(e.target.value);
  };

  const handleUnlockClick = () => {
    if (pin.length !== 4 || isNaN(pin)) {
      setErrorMessage('Please enter a valid 4-digit PIN.');
      return;
    }

    setErrorMessage('');
    onUnlock(pin);
  };

  return (
    <div className="pin-input-container" style={{ marginTop: '20px' }}>
      <h3>Enter your 4-digit PIN to unlock Shipping Requests</h3>
      <input
        type="text"
        value={pin}
        onChange={handlePinChange}
        maxLength="4"
        placeholder="PIN"
        style={{
          padding: '8px',
          fontSize: '16px',
          width: '100px',
          textAlign: 'center',
        }}
      />
      <button
        onClick={handleUnlockClick}
        style={{ marginLeft: '10px', padding: '8px 16px', fontSize: '16px' }}
      >
        Unlock
      </button>
      {errorMessage && (
        <div
          className="error-message"
          style={{ color: 'red', marginTop: '10px' }}
        >
          {errorMessage}
        </div>
      )}
    </div>
  );
}
