      ['20', 'Murphy, Lang and Ferry', 'Organized Explicit Access', 'backlog'],
    ].map(([id, name, description, status]) => ({
      id,
      name,
      description,
      status,
    }));
  }

  // Update the status of a card when it is dropped into a new swimlane
  updateCardStatus(cardId, newStatus) {
    this.setState(prevState => {
      const updatedClients = { ...prevState.clients };
      let cardToMove = null;

      // Find and remove the card from its old status
      Object.keys(updatedClients).forEach(status => {
        const cardIndex = updatedClients[status].findIndex(client => client.id === cardId);
        if (cardIndex !== -1) {
          // Create a copy of the card before removing
          cardToMove = { ...updatedClients[status][cardIndex] };
          updatedClients[status].splice(cardIndex, 1);
        }
      });

      // If the card is found, update its status and add it to the new swimlane
      if (cardToMove) {
        cardToMove.status = newStatus;
        if (!updatedClients[newStatus]) {
          updatedClients[newStatus] = [];
        }
        updatedClients[newStatus].push(cardToMove);
      }

      return { clients: updatedClients };
    });
  }

  // Initialize drag-and-drop behavior
  componentDidMount() {
    this.drake = dragula([
      this.swimlanes.backlog.current,
      this.swimlanes.inProgress.current,
      this.swimlanes.complete.current,
    ]);

    // Handle the drop event
    this.drake.on('drop', (el, target) => {
      const newStatus = target.getAttribute('data-status');
      const cardId = el.getAttribute('data-id');
      this.updateCardStatus(cardId, newStatus);
    });
  }

  // Clean up dragula when the component is unmounted
  componentWillUnmount() {
    if (this.drake) {
      this.drake.destroy();
    }
  }

  // Render the swimlane component for each status
  renderSwimlane(name, status, clients, ref) {
    return (
      <Swimlane
        name={name}
        status={status}
        clients={clients}
        dragulaRef={ref}
      />
    );
  }

  // Main render method
  render() {
    return (
      <div className="Board">
        <div className="container-fluid">
          <div className="row">
            <div className="col-md-4">
              {this.renderSwimlane('Backlog', 'backlog', this.state.clients.backlog, this.swimlanes.backlog)}
            </div>
            <div className="col-md-4">
              {this.renderSwimlane('In Progress', 'in-progress', this.state.clients.inProgress, this.swimlanes.inProgress)}
            </div>
            <div className="col-md-4">
              {this.renderSwimlane('Complete', 'complete', this.state.clients.complete, this.swimlanes.complete)}
            </div>
          </div>
        </div>
      </div>
    );
  }
}
