import React, { Component } from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import HomeTab from './HomeTab';
import Navigation from './Navigation';
import Board from './Board';
import './App.css';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      selectedTab: 'home',
      isUnlocked: false,
      userId: null,
      backlog: [
        { id: 1, status: 'backlog', priority: 1, name: 'Task 1' },
        { id: 2, status: 'backlog', priority: 2, name: 'Task 2' },
      ],
      inProgress: [
        { id: 3, status: 'in-progress', priority: 1, name: 'Task 3' },
      ],
      complete: [
        { id: 4, status: 'complete', priority: 1, name: 'Task 4' },
      ],
    };
  }

  changeTab = (tabName) => {
    if (tabName === 'home') {
      this.setState({ selectedTab: 'home', isUnlocked: false, userId: null });
    } else {
      this.setState({ selectedTab: tabName });
    }
  };

  handleUnlock = (pin, id) => {
    // Simple mock validation of PIN and user
    if (pin === '1234' && id) {
      this.setState({
        isUnlocked: true,
        selectedTab: 'shipping-requests',
        userId: id,
      });
    } else {
      alert('Invalid PIN or ID. Please try again.');
    }
  };

  handleCardChange = (cardId, oldStatus, newStatus, oldPriority, newPriority) => {
    const updatedState = { ...this.state };
    
    // Find the card and update its status and priority
    const updateColumn = (column) => {
      return column.map((card) => {
        if (card.id === cardId) {
          return { ...card, status: newStatus, priority: newPriority };
        }
        return card;
      });
    };

    updatedState[newStatus] = updateColumn(updatedState[newStatus]);

    // Remove the card from the old status column if status changed
    if (oldStatus !== newStatus) {
      updatedState[oldStatus] = updatedState[oldStatus].filter(
        (card) => card.id !== cardId
      );
    }

    this.setState(updatedState);
  };

  renderShippingRequests() {
    return <Board onCardChange={this.handleCardChange} backlog={this.state.backlog} inProgress={this.state.inProgress} complete={this.state.complete} />;
  }

  renderNavigation() {
    return (
      <Navigation
        onClick={this.changeTab}
        selectedTab={this.state.selectedTab}
        isUnlocked={this.state.isUnlocked}
      />
    );
  }

  renderTabContent() {
    const { selectedTab, isUnlocked } = this.state;
    if (selectedTab === 'shipping-requests' && isUnlocked) {
      return this.renderShippingRequests();
    }
    return <HomeTab onUnlock={this.handleUnlock} />;
  }

  render() {
    return (
      <div className="App">
        {this.renderNavigation()}
        <div className="App-body">
          {this.renderTabContent()}
        </div>
      </div>
    );
  }
}

export default App;
