import React from 'react';
import './Card.css';

export default class Card extends React.Component {
  render() {
    // Start with the base class 'Card'
    let className = 'Card';
    
    // Add status-specific classes based on the status prop
    if (this.props.status === 'backlog') {
      className += ' Card-grey';   // Add grey class for backlog
    } else if (this.props.status === 'in-progress') {
      className += ' Card-blue';   // Add blue class for in-progress
    } else if (this.props.status === 'complete') {
      className += ' Card-green';  // Add green class for complete
    }

    return (
      <div className={className} data-id={this.props.id} data-status={this.props.status}>
        <div className="Card-title">{this.props.name}</div>
      </div>
    );
  }
}
