import React from 'react';
import Card from './Card';
import './Swimlane.css';
import Dragula from 'dragula';

export default class Swimlane extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      clients: this.props.clients,
    };
  }

  // Update card status when dropped in a new swimlane
  updateCardStatus(cardId, newStatus) {
    const updatedClients = this.state.clients.map(client => {
      if (client.id === cardId) {
        client.status = newStatus;  // Update the card's status based on the new swimlane
      }
      return client;
    });

    this.setState({ clients: updatedClients });
  }

  // Initialize drag-and-drop functionality with Dragula
  componentDidMount() {
    const container = this.props.dragulaRef.current;
    
    if (container) {
      const drake = Dragula([container]);

      // Handle when a card is dropped in another swimlane
      drake.on('drop', (el, target) => {
        const cardId = el.dataset.id;
        const newSwimlane = target.dataset.swimlane;  // Target swimlane is in the data attribute
        this.updateCardStatus(cardId, newSwimlane);
      });
    }
  }
}
